import React from "react";
//import css
import '../assets/css/login.css';
//import imagenes
import logo from '../assets/img/logo.png';
//SERVICIOS
import { } from '../services/apirest'

class login extends React.Component {

    //AQUI SE CREAN LAS VARIABLES ESTATICAS QUE REQUIERE LA API
    state = {
        form: {
            "usuario": "",
            "password": "",
        },
        error: false,
        errorMsg: ""
    }

    manejadorSubmit = e => {
        e.preventDefault();
    }


    manejadorChange = async e => {
        await this.setState({
            form: {
                ...this.state.form,
                [e.target.name]: e.target.value
            }
        })
        console.log(this.state.form)
    }


    manejadorBoton() {
        console.log("enviado")
    }


    render() {
        return (
            <React.Fragment>
                <div className="container">
                    <div className="row">
                        <div className="col-md-11 mt-60 mx-md-auto">
                            <div className="login-box bg-white pl-lg-5 pl-0">
                                <div className="row no-gutters align-items-center">
                                    <div className="col-md-6">
                                        <div className="form-wrap bg-white">
                                            <form onSubmit={this.manejadorSubmit}>
                                                <h4 align="center" className="btm-sep pb-3 mb-5">Iniciar Sesión</h4>
                                                <input type="text" id="usuario" className="fadeIn second" name="usuario" placeholder="usuario" onChange={this.manejadorChange} />
                                                <input type="password" id="password" className="fadeIn third" name="password" placeholder="Contraseña" onChange={this.manejadorChange} />
                                                <input type="submit" className="fadeIn fourth" value="Iniciar Sesión" onClick={this.manejadorBoton} />
                                            </form>
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="content text-center">
                                            <div className="border-bottom pb-5 mb-2">
                                                <img src={logo} alt="" />
                                            </div>
                                            <small>@Desarrolado por Abdón Calderón</small>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </React.Fragment>
        );
    }
}

export default login
